import React from 'react';
import UserMenu from './UserMenu';
import { DbHelper } from '../DbHelper';
class Settings extends React.Component {
    constructor() {
        super();
        this.state = {
            errors: '',
            success: '',
            user: JSON.parse(sessionStorage.getItem("_u")),
            profileForm: {
                validation: {
                    rules: {
                        name: ['required']
                    },
                    messages: {},
                    valid: false,
                },
                formValue: {},
            }
        }
    }
    componentDidMount() {
        document.profileform.name.value = this.state.user.name;
    }
    updateProfileAction = () => {
        const formData = {};
        const errorMessages = {};
        var formValidStatus = true;
        const formElement =  Array.from(document.profileform.elements).map(({name,value})=>{
          formData[name] = value;
          let validation = this.state.profileForm.validation.rules[name];
          if (validation) {
            errorMessages[name] = {};
            if (validation.indexOf('required') > -1) {
              if (value == '') {
               errorMessages[name]['required']='field is required';
               formValidStatus = false;
              }
            }
          }
          
          return {
            name:name,
            value:value
          }
        });
    }
    render() {
        return (
            <React.Fragment>
                <div className="row">
                    <div className="col-5">
                        <h4>Settings</h4>
                    </div>
                    <div className="col-7 text-right">
                        <UserMenu />
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        {this.state.success &&
                            <div class="alert alert-success text-center">
                                {this.state.success}
                            </div>
                        }
                        {this.state.errors &&
                            <div class="alert alert-danger text-center">
                                {this.state.errors}
                            </div>
                        }
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <div className="card">
                            <div className="card-body">
                                <h6 className="card-title">Profile Update</h6>
                                <div className="card-text">
                                    <form name="profileform">
                                        <div className="form-group">
                                            <label >Name</label>
                                            <input type="text" name="name" className="form-control" />
                                        </div>
                                    </form>
                                    <div className="form-group">
                                        <input type="button" onClick={this.updateProfileAction} className="btn btn-success" value="Save" />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="col">
                        <div className="card">
                            <div className="card-body">
                                <h6 className="card-title">Change Password</h6>
                                <div className="card-text">
                                    <form name="passwordform">
                                        <div className="form-group">
                                            <label >Old Password</label>
                                            <input type="password" className="form-control" />
                                        </div>
                                        <div className="form-group">
                                            <label >New Password</label>
                                            <input type="password" className="form-control" />
                                        </div>
                                        <div className="form-group">
                                            <label >Confirm Password</label>
                                            <input type="password" className="form-control" />
                                        </div>
                                    </form>
                                    <div className="form-group">
                                        <input type="button" className="btn btn-success" value="Save" />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}
export default Settings;
