import React from 'react';

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            validation: {
                rules: {},
                messages: {},
                valid: false,
            },
            formValue: {}
        }
    }
    componentDidMount() {

    }
    submitHandeler = ($event) => {

        $event.preventDefault();
        const formData = {};
        const errorMessages = {};
        var formValidStatus = true;
        const formElement = Array.from(document[this.props.config.name].elements).map(({ name, value }) => {
            formData[name] = value;
            let validation = this.state.validation.rules[name];
            if (validation) {
                errorMessages[name] = {};
                if (validation.indexOf('required') > -1) {
                    if (value == '') {
                        errorMessages[name]['required'] = 'field is required';
                        formValidStatus = false;
                    }
                }
                if (value !== '' && validation.indexOf('email') > -1) {
                    if (!(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/).test(value)) {
                        errorMessages[name]['email'] = 'Email is not valid';
                        formValidStatus = false;
                    }
                }
                if (value !== '' && validation.find((i) => i.startsWith('min'))) {
                    let minValue = validation.find((i) => i.startsWith('min')).split(':');
                    minValue = parseInt(minValue[1]);
                    if (value.length < minValue) {
                        errorMessages[name]['min'] = 'minimum ' + minValue + ' character is required';
                        formValidStatus = false;
                    }
                }
                if (value !== '' && validation.find((i) => i.startsWith('max'))) {
                    let maxValue = validation.find((i) => i.startsWith('max')).split(':');
                    maxValue = parseInt(maxValue[1]);
                    if (value.length > maxValue) {
                        errorMessages[name]['max'] = 'max ' + maxValue + ' character is required';
                        formValidStatus = false;
                    }
                }
            }

            return {
                name: name,
                value: value
            }
        });
        setTimeout(() => {
            this.setState(prevState => ({
                ...prevState,
                validation: {
                    ...prevState.validation,
                    messages: errorMessages,
                    valid: formValidStatus,
                },
                formValue: formData
            }));
            if (this.state.validation.valid === true) {
                this.props.onSubmit(this.state);
                // this.props.config.submitHandeler(this.state.formValue);
            } else {
                // alert('error');
            }
        }, 50);
        // this.props.config.submitHandeler();
    }
    render() {

        return (
            <form name={this.props.config.name} onSubmit={this.submitHandeler}>
                {Object.keys(this.props.config.elements).map((i) => {
                    let formElement = this.props.config.elements[i];
                    return <div className="form-group" key={i}>
                        <label>{i}</label>
                        {(() => {
                            switch (formElement.type) {
                                case "text":
                                    return <input type="text" className="form-control" name={i} defaultValue={formElement.value} />
                                case "email":
                                    return <input type="email" className="form-control" name={i} defaultValue={formElement.value} />
                                case "password":
                                    return <input type="password" className="form-control" name={i} defaultValue={formElement.value} />
                            }
                        })()}
                        {(this.state.validation.messages[formElement.name] && this.state.validation.messages[formElement.name].required) &&
                            <p className="form-err">
                                {this.state.validation.messages[formElement.name].required}
                            </p>}
                        {(this.state.validation.messages[formElement.name] && this.state.validation.messages[formElement.name].email) &&
                            <p className="form-err">
                                {this.state.validation.messages[formElement.name].email}
                            </p>
                        }
                    </div>
                })}
                <div className="form-group">
                    {this.props.config.buttons.map((i) => {
                        return <input type={i.type} value={i.value} className={i.cssClass} />
                    })}
                </div>
            </form>
        );
    }
}

export default Form;
