import React from 'react';

class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            validation: {
                rules: {},
                messages: {},
                valid: false,
            },
            formValue: {}
        }
    }
    componentDidMount() {

    }
    submitHandeler = ($event)=>{

        $event.preventDefult();
        this.props.config.submitHandeler();
    }
    render() {

        return (
            <form name={this.props.config.name} onSubmit={this.submitHandeler}>
                {Object.keys(this.props.config.elements).map((i) => {
                    let formElement = this.props.config.elements[i];
                    return <div className="form-group">
                        <label>{i}</label>
                        {(() => {
                            switch (formElement.type) {
                                case "text":
                                    return <input type="text" className="form-control" name={i} value={formElement.value} />
                                case "email":
                                    return <input type="email" className="form-control" name={i} value={formElement.value} />
                                case "password":
                                    return <input type="password" className="form-control" name={i} value={formElement.value} />
                            }
                        })()}
                    </div>
                })}
                <div className="form-group">
                    {this.props.config.buttons.map((i)=>{
                        return <input type={i.type} value={i.value} className={i.cssClass} />
                    })}
                </div>
            </form>
        );
    }
}

export default Form;
