import React from 'react';
import UserMenu from './UserMenu';
import {DbHelper} from '../DbHelper';
import {Link} from 'react-router-dom'
class PublicProfile extends React.Component {

    constructor() {
        super();
        this.state = {
            errors:'',
            success:'',
            user:{}
          }
    }
    componentDidMount() {
        const {id} = this.props.match.params;
        let user = DbHelper.find('users',[['_id',parseInt(id)]]);
        user['friends'] = DbHelper.filter('friend_requests',[['user_id',parseInt(id)],['status','A']]).map((i)=>{
            return {
                ...i,
                friend:DbHelper.find('users',[['_id',parseInt(i.friend_id)]])
            }
        });
        console.log(user);
        this.setState({'user':user});
    }
    render() {
        
        return (
            <React.Fragment>
      <div className="row">
      <div className="col-5">
        <h4>{this.state.user.name}</h4>
      </div>
      <div className="col-7 text-right">
      <UserMenu/>
      </div>
      </div>
      <div className="row">
      <div className="col-12">
          {this.state.success &&
          <div class="alert alert-success text-center">
          {this.state.success}
          </div>
          }
          {this.state.errors &&
          <div class="alert alert-danger text-center">
          {this.state.errors}
          </div>
          }
          {this.state.user.friends &&
          <div className="card">
              <div className="card-body">
                  <h5 className="card-title">Friends</h5>
                  <div className="row">
                      {this.state.user.friends.map((i)=>{
                          return (
                              <div className="card">
                                  <div className="card-body">
                                  <Link to={'/profile/'+i._id}> {i.friend.name}</Link> 
                                  </div>
                              </div>
                          )
                      })}
                  </div>
              </div>
          </div>
          }
      </div>
      </div>
      </React.Fragment>
        )
    }
}

export default PublicProfile;
