import React from 'react';
import UserMenu from './UserMenu';
import {DbHelper} from '../DbHelper';
class PublicProfile extends React.Component {

    constructor() {
        super();
        this.state = {
            errors:'',
            success:'',
            user:{}
          }
    }
    componentDidMount() {
        const {id} = this.props.match.params;
        this.setState({'user':DbHelper.find('users',[['_id',parseInt(id)]])});
        console.log(DbHelper.find('users',[['_id',parseInt(id)]]));
    }
    render() {
        
        return (
            <React.Fragment>
      <div className="row">
      <div className="col-5">
      <h4>Profile</h4>
      </div>
      <div className="col-7 text-right">
      <UserMenu/>
      </div>
      </div>
      <div className="row">
      <div className="col-12">
          {this.state.success &&
          <div class="alert alert-success text-center">
          {this.state.success}
          </div>
          }
          {this.state.errors &&
          <div class="alert alert-danger text-center">
          {this.state.errors}
          </div>
          }

      </div>
      </div>
      </React.Fragment>
        )
    }
}

export default PublicProfile;
